import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { ExternalLink, Github, Zap, Brain, Camera, Film, Calendar } from 'lucide-react';

const Projects = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const projects = [
    {
      id: 1,
      title: "Self-Healing Backend System",
      description: "A robust error-handling pipeline that intercepts system errors in real-time, logs them with detailed context, and converts them into vector embeddings using FAISS for semantic retrieval—cutting manual debugging by 40% and improving response time for non-technical users.",
      longDescription: "Built with GPT-4o to generate clear, plain-English explanations and actionable fix suggestions—designed to support users without deep technical expertise. Integrated a chatbot assistant with the system's database, enabling users to ask follow-up questions, receive clarification, and track persistent or recurring issues over time. Designed for extensibility beyond databases, allowing seamless integration into any backend system where automatic error diagnostics and support are critical.",
      technologies: ["GPT-4o", "FAISS", "Vector Search", "LangChain", "Python", "RAG", "Chatbot"],
      icon: Brain,
      color: "from-blue-500 to-purple-600",
      bgColor: "bg-blue-50 dark:bg-blue-900/20",
      date: "June 2025 - Present",
      github: "https://github.com/siddhantamohanty/self-healing-backend",
      live: null,
      featured: true
    },
    {
      id: 2,
      title: "Photobot",
      description: "An AI assistant that answers user questions and recommends optimal camera settings based on PDF manuals of multiple camera models.",
      longDescription: "Used LangChain + FAISS to semantically retrieve relevant content by environment (e.g., 'Astrophotography', 'sports photography' or 'indoor concert') and respond with camera-specific suggestions using GPT-4. Created an intuitive frontend to query manuals for DSLRs, mirrorless, or point-and-shoots and receive detailed, model-specific configuration tips (ISO, shutter speed, white balance, etc.).",
      technologies: ["LangChain", "FAISS", "GPT-4", "React", "Python", "PDF Processing", "RAG"],
      icon: Camera,
      color: "from-green-500 to-teal-600",
      bgColor: "bg-green-50 dark:bg-green-900/20",
      date: "May 2025 - Present",
      github: "https://github.com/siddhantamohanty/photobot",
      live: null,
      featured: true
    },
    {
      id: 3,
      title: "Swipeflix",
      description: "A web application designed to help users discover movies through an engaging, swipe-based interface inspired by popular dating apps. The platform aims to make movie selection fun and interactive.",
      longDescription: "Simplifies the process of finding movies to watch by combining a visually rich, cinematic UI with personalized suggestion algorithms. Built with React for the frontend, Tailwind CSS for styling, and JavaScript for application logic. Utilizes browser local storage to save user preferences and quiz results, ensuring a personalized experience without requiring user accounts.",
      technologies: ["React", "Tailwind CSS", "JavaScript", "Local Storage", "Movie API", "UI/UX"],
      icon: Film,
      color: "from-red-500 to-pink-600",
      bgColor: "bg-red-50 dark:bg-red-900/20",
      date: "January 2025 - Present",
      github: "https://github.com/siddhantamohanty/swipeflix",
      live: "https://swipeflix.siddhantamohanty.com",
      featured: true
    },
    {
      id: 4,
      title: "CourseScheduler",
      description: "Developed an academic planning tool using Node.js and MongoDB to manage complex degree requirements for students with multiple majors/minors.",
      longDescription: "Built a React frontend using Tailwind CSS and NLP (Python) for conflict-free, personalized graduation planning. Helps students navigate complex degree requirements and optimize their course schedules.",
      technologies: ["Node.js", "MongoDB", "React", "Tailwind CSS", "NLP", "Python"],
      icon: Calendar,
      color: "from-orange-500 to-yellow-600",
      bgColor: "bg-orange-50 dark:bg-orange-900/20",
      date: "October 2024",
      github: "https://github.com/siddhantamohanty/course-scheduler",
      live: null,
      featured: false
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6
      }
    }
  };

  return (
    <section id="projects" className="py-20 bg-white dark:bg-dark-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8 }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl md:text-5xl font-bold text-gray-900 dark:text-white mb-6">
            What I'm <span className="gradient-text">Building</span>
          </h2>
          <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Projects that combine creativity with technical depth to solve real-world problems
          </p>
        </motion.div>

        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
          className="grid md:grid-cols-2 gap-8"
        >
          {projects.map((project, index) => (
            <motion.div
              key={project.id}
              variants={cardVariants}
              className={`project-card ${project.featured ? 'md:col-span-2' : ''}`}
            >
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <div className={`w-12 h-12 rounded-lg flex items-center justify-center ${project.bgColor}`}>
                    <project.icon className={`w-6 h-6 bg-gradient-to-r ${project.color} bg-clip-text text-transparent`} />
                  </div>
                  <div>
                    <h3 className="text-xl font-bold text-gray-900 dark:text-white">{project.title}</h3>
                    <div className="flex items-center text-sm text-gray-500 dark:text-gray-400">
                      <Calendar className="w-4 h-4 mr-1" />
                      {project.date}
                    </div>
                  </div>
                </div>
                <div className="flex space-x-2">
                  {project.github && (
                    <motion.a
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.95 }}
                      href={project.github}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="p-2 bg-gray-100 dark:bg-dark-700 rounded-lg text-gray-600 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition-colors duration-200"
                    >
                      <Github className="w-5 h-5" />
                    </motion.a>
                  )}
                  {project.live && (
                    <motion.a
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.95 }}
                      href={project.live}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="p-2 bg-gray-100 dark:bg-dark-700 rounded-lg text-gray-600 dark:text-gray-300 hover:text-primary-600 dark:hover:text-primary-400 transition-colors duration-200"
                    >
                      <ExternalLink className="w-5 h-5" />
                    </motion.a>
                  )}
                </div>
              </div>

              <p className="text-gray-600 dark:text-gray-300 mb-4 leading-relaxed">
                {project.description}
              </p>

              {project.featured && (
                <div className="mb-4 p-4 bg-gray-50 dark:bg-dark-700 rounded-lg">
                  <p className="text-sm text-gray-600 dark:text-gray-300 leading-relaxed">
                    {project.longDescription}
                  </p>
                </div>
              )}

              <div className="flex flex-wrap gap-2">
                {project.technologies.map((tech, techIndex) => (
                  <span
                    key={techIndex}
                    className="skill-tag"
                  >
                    {tech}
                  </span>
                ))}
              </div>

              {project.featured && (
                <div className="mt-4 flex items-center text-sm text-primary-600 dark:text-primary-400">
                  <Zap className="w-4 h-4 mr-2" />
                  Featured Project
                </div>
              )}
            </motion.div>
          ))}
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={inView ? { opacity: 1, y: 0 } : {}}
          transition={{ duration: 0.8, delay: 0.6 }}
          className="text-center mt-12"
        >
          <motion.a
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            href="https://github.com/siddhantamohanty"
            target="_blank"
            rel="noopener noreferrer"
            className="inline-flex items-center px-6 py-3 bg-gray-900 dark:bg-white text-white dark:text-gray-900 rounded-lg font-semibold hover:bg-gray-800 dark:hover:bg-gray-100 transition-all duration-300"
          >
            <Github className="w-5 h-5 mr-2" />
            View More on GitHub
          </motion.a>
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
